void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord/iResolution.xy);
    fragColor = texture(iChannel0, uv);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = vec2(fragCoord/iResolution.xy);

    vec2 r = vec2(textureSize(iChannel0, 0));

    vec2 d0100 = vec2(1, 0) / r;
    vec2 d0001 = vec2(0, 1) / r;

    vec2 t0101 = uv;

    vec2 t0000 = t0101 - d0100 - d0001;
    vec2 t0001 = t0000 + d0001;
    vec2 t0010 = t0001 + d0001;

    vec2 t0100 = t0101 - d0001;
    vec2 t0110 = t0101 + d0001;

    vec2 t1000 = t0101 + d0100 - d0001;
    vec2 t1001 = t1000 + d0001;
    vec2 t1010 = t1000 + d0001;

    vec4 c0000 = texture(iChannel0, t0000);
    vec4 c0001 = texture(iChannel0, t0001);
    vec4 c0010 = texture(iChannel0, t0010);
    vec4 c0100 = texture(iChannel0, t0100);
    vec4 c0101 = texture(iChannel0, t0101);
    vec4 c0110 = texture(iChannel0, t0110);
    vec4 c1000 = texture(iChannel0, t1000);
    vec4 c1001 = texture(iChannel0, t1001);
    vec4 c1010 = texture(iChannel0, t1010);

    float K0000 = 1.0;
    float K0001 = 2.0;
    float K0010 = 1.0;
    float K0100 = 0.0;
    float K0101 = 0.0;
    float K0110 = 0.0;
    float K1000 = -1.0;
    float K1001 = -2.0;
    float K1010 = -1.0;    

    vec4 Du = 
          K0000 * c0000
        + K0001 * c0001
        + K0010 * c0010
        + K0100 * c0100
        + K0101 * c0101
        + K0110 * c0110
        + K1000 * c1000
        + K1001 * c1001
        + K1010 * c1010;

    vec4 Dv = 
          K0000 * c0000
        + K0100 * c0001
        + K1000 * c0010
        + K0001 * c0100
        + K0101 * c0101
        + K1001 * c0110
        + K0010 * c1000
        + K0110 * c1001
        + K1010 * c1010;

    float du = (Du.x + Du.y + Du.z) / 3.0;
    float dv = (Dv.x + Dv.y + Dv.z) / 3.0;

    //fragColor = vec4(du, dv, 0.0, 0.0);
    //fragColor = vec4(du, dv, 0.0, 0.0);
    vec2 D = vec2(-dv, du);
    vec2 st = uv - 0.01 * D;    
    //fragColor = vec4(-D, 0.0, 1.0);
    if (iFrame == 0) {
        fragColor = texture(iChannel0, st);
    }
    else {
        fragColor = texture(iChannel1, st);
    }
    //fragColor = vec4(du, dv, 0.0, 0.0);
}
