void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    // Normalize coordinates and apply aspect ratio
    vec2 uv = fragCoord.xy / iResolution.xy;
    uv.x *= iResolution.x / iResolution.y;

    // Center and zoom in
    vec2 c = vec2(-0.5, 0.0) + (uv - 0.5) * 2.0;

    // Animate the Julia set
    float t = iTime * 0.2;
    vec2 k = vec2(cos(t) * 0.5, sin(t) * 0.5);

    // Iterate the Julia set
    int iterations = 0;
    const int maxIterations = 100;
    while (iterations < maxIterations) {
        c = vec2(c.x * c.x - c.y * c.y, 2.0 * c.x * c.y) + k;
        if (length(c) > 4.0) break;
        iterations++;
    }

    // Colorize the Julia set based on iterations
    vec3 color = vec3(0.0);
    if (iterations < maxIterations) {
        float v = float(iterations) / float(maxIterations);
        color = mix(vec3(0.2, 0.0, 0.5), vec3(1.0, 0.4, 0.0), v);
    }

    fragColor = vec4(color, 1.0);
}
