void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time-dependent parameters
    float timeFactor = mod(iTime, 2.0)-1.0;

    // Create a vertical gradient that moves over time
    float gradient = uv.y + timeFactor;

    //Truncate Excess Gradient
    if (gradient < 0.0) {
        gradient = 1.0;
    }

    // Interpolating between two colors based on the moving gradient
    vec3 color1 = vec3(0.5, 1.0, 0.5);  // Red color
    vec3 color2 = vec3(0.0, 0.0, 0.0);  // Blue color
    vec3 mixedColor = mix(color1, color2, gradient);  // Interpolate between the two colors

    // Output to screen
    fragColor = vec4(mixedColor, 1.0);
}
