{
    fragColor = texelFetch(iChannel0, ivec2(fragCoord), 0);
}

#define M_PI 3.1415926535897932384626433832795

vec3 colOn = vec3(1.0,0.0,0.0);
vec3 colOff = vec3(0.0);
const float pSigAm = 300.; 

float hash13(vec3 p3) 
{
	p3  = fract(p3 * .1031);
    p3 += dot(p3, p3.yzx + 19.19);
    return fract((p3.x + p3.y) * p3.z);
}

ivec2 snap2axis(vec2 dir)
{
    if(abs(dir.x) > abs(dir.y))    
        return dir.x < 0.0 ? ivec2(-1.0,0.0) : ivec2(1.0,0.0);
    else
        return dir.y < 0.0 ? ivec2(0.0,-1.0) : ivec2(0.0,1.0);    
}

vec2 total_dir(vec2 seed)
{
    vec2 total;
    int i=8;
    for(; i<=64; i+=8)
    {
        float hash = 2.0*M_PI*hash13(vec3(ivec2(seed/vec2(i)),iFrame/i));
        vec2 randDir = vec2(cos(hash),sin(hash));
        total += randDir;
    }

    total/=vec2(float(i)/1.);
    total = vec2(snap2axis(total));
    return total;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 seed = fragCoord;
    vec4 last = texelFetch(iChannel0, ivec2(fragCoord), 0);
    //hasFood, patrolSig, foodSig, isActive
    vec4 state = vec4(last.w==1.?0.:last.x,last.y-1.0/pSigAm,last.z-1.0/pSigAm,0.0); 

    if(iFrame==0 || keyPressed(82,iChannel1) > 0.5)
    {
        uvec2 V = uvec2(fragCoord);
        float h = hash13(vec3(fragCoord,iFrame));
        if(h>0.999) state = vec4(0,1,0,1);
    }
    else
    {
        for(float i=0.0; i<2.0; i+=0.5)
        {
            ivec2 off = ivec2(cos(i*M_PI),sin(i*M_PI));
            ivec2 nseed = ivec2(fragCoord)+off;
            vec4 neibr = texelFetch(iChannel0, nseed, 0);
            if(neibr.w > 0.5)
            {
                vec2 randDir = total_dir(vec2(nseed));
                if(off==-ivec2(randDir))
                {
                    state = vec4(last.x,1,0,1);
                    break;
                }
            }
        }     
    }

    fragColor = vec4(state);
}
