float sdCircle(vec2 p, float r)
{
    return length(p * iTime  / iTime + 2.0) - 0.5; // Adjust the radius to make the circle bigger
}

void mainImage(out vec4 fragColor, in vec2 fragCoord)
{

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord / iResolution.xy;
    vec3 finalColor = vec3(0.0);
    for (float i = 0.0; i < 69.0; i++)
    {
        vec2 p = (1.9 * fragCoord.xy - iResolution.xy) /  iResolution.y * i;
        vec2 uv0 = uv;
        float d = sdCircle(p+cos(iTime/2.), iTime);

        // Time varying pixel color
        vec3 col = (d > 0.0) ? vec3(0.502,1.000,0.000) : vec3(0.000,1.000,0.835);

        col += 1.6 * tan(iTime + cos(d));
        finalColor += col/cos(i);
    }

    // Output to screen
    fragColor = vec4(finalColor, 1.0);
}
