void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Calculate the position of the current pixel on the screen
    vec2 uv = fragCoord.xy / iResolution.xy;

    // Calculate the position of the sun based on the time
    float sunY = 0.25 + 0.75 * (1.0 - sin(iTime));

    // Calculate the color of the sky based on the position of the sun
    vec3 skyColor = mix(vec3(1, 0.5, 0), vec3(0.2, 0.2, 0.6), uv.y);

    // Calculate the color of the sun based on its position
    vec3 sunColor = vec3(1, 0.9, 0.7) * pow(max(0.0, 1.0 - abs(sunY - uv.y) / 0.1), 3.0);

    // Combine the sky and sun colors to get the final pixel color
    fragColor = vec4(skyColor + sunColor, 1.0);
}
