#define TAU 6.28318530718  // 2 PI

// See https://iquilezles.org/articles/palettes for more information
vec3 pal( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b * cos( TAU * (c * t + d) );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = fragCoord.xy / iResolution.xy;

    // animate
    float t = (.5 - .5 * cos(iTime)) * 0.25;

    // compute colors
    vec3 col = pal( p.x + p.y / 16., vec3(0.5,0.5,0.5),vec3(-0.5,-0.5,-0.5),vec3(1.0,1.0,1.0),vec3(-t, 0.0, t) );

	fragColor = vec4( col, 1.0 );
}
