     bool circle(vec2 uv, vec2 center, float radius) {
        return (uv.x - center.x) * (uv.x - center.x) + (uv.y - center.y) * (uv.y - center.y) < radius*radius; 
    }

    void mainImage(out vec4 fragColor, in vec2 fragCoord) {

