float n_tau = 6.2831;

float f_n_dist_sqr_swiggle(vec2 o, float n_amp){

    float n_max = max(abs(o.x), abs(o.y));
    float n_min = min(abs(o.x), abs(o.y));
    return n_max + sin(n_min*n_tau)*n_amp;
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 o_trpino = (fragCoord.xy - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_trpino_nooffset = (fragCoord.xy )/ iResolution.y;
    vec2 o_trmono = (iMouse.xy - iResolution.xy*0.5)/ iResolution.y;
    vec2 o_trmono_nooffset = (iMouse.xy )/ iResolution.y;
    o_trpino*= (sin(iTime*0.1)*0.5+0.5)*20.+2.;
    float n_dist = length(o_trpino);
    float n = f_n_dist_sqr_swiggle(o_trpino, sin(iTime*n_tau*0.5+n_dist*0.5)*0.5);

    fragColor = vec4(sin(n*n_tau*2.));
}
