// (golfed) variant of "Jellyroid " by Alpaga. https://shadertoy.com/view/dsXSRs

void mainImage( out vec4 O, vec2 I ) {
    float t = .5*iTime, w = 1.;
    vec3  d = normalize( vec3( I+I, 0 ) - iResolution.xyy ), // Ray direction
          p = 5.*( vec3(sin(t), -t, cos(t)) + d );           // Ray origin
 // p += d*fract(sin(dot(d + p, vec3(1)))*1e5)*.1;           // optional dithering (Shane)

    for( O*=0.; O.a++ < 1e2; w*=.98 )                        // March
        abs( dot( sin( p +=.1*d ), cos(p.yzx) ) ) < .1 ? O += w/20. : O;
}             // Gyroid
          // variant:  abs ( dot(,)  - 1.4 ) < .1
