float side = 30.;
vec2 corner = vec2(-0., 0.);

void mainImage(out vec4 col_out, in vec2 frag_cord)
{
    vec2 uv = (frag_cord.xy / iResolution.xy) * 2.0 - 1.0;

    uv.y *= iResolution.y / iResolution.x;
    uv   += vec2(1,1) * iTime / 20.0;
    uv   *= 100.0;
    uv    = floor(uv);

    vec2 c = corner + uv * side / 100.0;

    float cv = c.x * c.x + c.y * c.y;

    col_out = vec4(1.,1.,1.,0.) * float(abs(mod(cv, 3.0)) <= .5);
}
