vec2 hyper(vec2 uv){
    float x = (2.0 * uv.x)/(1.0+uv.x*uv.x+uv.y*uv.y);
    float y = 0.0;
    return vec2(x,y);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    //uv -= 0.5;
    float tile = 8.0;
    float zoom = 1.025;
    tile *= uv.x * zoom + uv.y;
    float s_time = iTime * 0.1;

    uv = fract(uv * tile + s_time);
    uv -= 0.5;


    float p = length(uv);
    p += abs(sin(iTime*0.2))*0.1;






    vec3 col = vec3(p);
    fragColor = vec4(col,1.0);
}
