#define shake cos(iTime*.1 * escada(y, .1))

#define zoom .2 * sin(iTime * .3) 

float escada(float x, float w){
    return floor(x / w) * w;
}

vec3 bg(vec2 p){

    float y = length(p) * 2. + zoom;
    float x = atan(p.x, p.y);

    float a = sin(x * 2. + iTime * .5);
    float b = cos(y * 2. + iTime * .7);

    float c = sin(x * 4. - iTime * .3);
    float d = cos(y * 4. - iTime * .1);

    vec3 col = vec3(a * c, b * d, (c + d) * .5);    

    return col;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord ){
    vec2 uv = (fragCoord - .5 * iResolution.xy);

    float mask = sin(uv.x * 6. + uv.y * 6. + iTime * .2) * .5 + .5; 
    uv *= mask;     

    fragColor = vec4(bg(uv), 1.);
}
