void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (fragCoord.xy / iResolution.xy) - 0.5;
    uv.x *= iResolution.x / iResolution.y;
    float time = iTime * 0.5 + 0.25;
    vec2 mouse = iMouse.xy / iResolution.xy;

    float frequency = 4.0;
    float amplitude = 0.05;
    float speed = 0.1;
    float displacement = 0.2;

    vec2 pos = vec2(uv.x, uv.y + sin(uv.x * frequency + time * speed) * amplitude);
    pos.x += sin(pos.y * frequency + time * speed * 2.0) * amplitude * displacement;

    float r = 0.5 + 0.5 * sin(time * 0.2 + pos.x * 10.0);
    float g = 0.5 + 0.5 * sin(time * 0.5 + pos.y * 20.0);
    float b = 0.5 + 0.5 * sin(time * 0.8 + (pos.x + pos.y) * 15.0);

    float d = distance(pos, mouse);
    float alpha = smoothstep(0.5, 0.0, d);

    fragColor = vec4(r, g, b, alpha);
}
