vec3 mosaic(vec2 uv){
  vec3 col = vec3(0);
  vec2 offset = vec2(0);
  for(int k = 0; k < 9; k++){
        offset =
            vec2(-uv.x,uv.y)*2.;
        uv =
            abs(fract(uv.yx-offset)-.5);
        if(uv.y > uv.x) col =
            vec3(col.yz,abs(uv.x-uv.y));
        else uv -= length(uv);
        uv /= 1.+fract(uv);
    }
    return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    fragColor = vec4(0.0);
    vec2 uv = (fragCoord)/iResolution.y/2.0;
    uv += vec2(iTime/2.0,iTime/3.0)/8.0;
    if(iMouse.z>.5)
    uv = uv.xy + iMouse.xy / iResolution.xy/4.;
    vec3 col = mosaic(uv);
    fragColor = vec4(min(col*2.,vec3(1.)),1.0);
}
