void mainImage( out vec4 fragColor, in vec2 fragCoord ){  
    vec2 uv = (fragCoord/iResolution.xy-vec2(0.5,.2))*0.07;
    float s1 = .05, s2 = .3, s3 =.3, s4 =.6; //shape
    float r = (s1 + s2*pow(abs(atan(uv.x, -uv.y))/3.1416,6.))/8.;    
    float fd = (1. -  min(abs(s3 - pow(length(uv)/r,s4)),1.))+sin(iTime*100.)*.01;
    fragColor = fd*vec4(0.,.2,.9,1.);
}
