renderpass.code:
float Club(vec2 w)
{
    float Sz=.05, dx=.04, dy=.06;
    return min(length(w-vec2(dx,.0)),
        min(length(w-vec2(-dx,.0)),
        min(length(w-vec2(.0,dy)),
        max(abs(w.x*2.),abs(w.y+Sz)))))/Sz;
}

float Heart(vec2 w)
{
    float Sz=.05, dx=.04, dy=.06;
    float y1=.035, y2=.01;
    return min(.8*length(w-vec2(dx,y1)),
        min(.8*length(w-vec2(-dx,y1)),
        .6*(abs(w.x)+abs(w.y+y2))))/Sz;
}

float Spade(vec2 w)
{
    float Sz=.05, dx=.04, dy=.06;
    float y1=-.035+.02, y2=-.01-.02;
    return min(.8*length(w-vec2(dx,y1)),
        min(.8*length(w-vec2(-dx,y1)),
        min(.6*(abs(w.x)+abs(w.y+y2)),
        max(abs(w.x*2.),abs(w.y+.05))))
        )/Sz;
}

float Diamond(vec2 w)
{
    return (.7*abs(w.x)+.5*abs(w.y))/.05;
}

vec2 Off(float a)
{
    return vec2(.3*sin(iTime+a),0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    vec2 w = (fragCoord - .5*iResolution.xy) / iResolution.x;

    // Time varying pixel color
    vec3 col = .3 + .3*cos(iTime+uv.xyx+vec3(0,2,4));

    vec3 Red=vec3(1,0,0), Blk=vec3(0);

    col = Club(w+Off(0.))     < 1. ? Blk : col;
    col = Heart(w+Off(3.1))   < 1. ? Red : col;
    col = Spade(w+Off(1.6))   < 1. ? Blk : col;
    col = Diamond(w+Off(4.7)) < 1. ? Red : col;

    // Output to screen
    fragColor = vec4(col,1.0);
}
