const float PI = 3.14159265;

float func(float x)
{
    return .33*cos(x * PI + 0.4);
}


float func_derivative(float x)
{
    return -.33*sin(x * PI + 0.4) * PI;
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.*fragCoord-iResolution.xy)/iResolution.y;
    vec2 mouse = (2.*iMouse.xy-iResolution.xy)/iResolution.y;

    if(length(iMouse.xy) < 10.) mouse = vec2(0);

    float pix_size = 2. / iResolution.y;
    vec3 color = vec3(0);


    float axes = .25 * smoothstep(1.5*pix_size, pix_size/2., min(abs(uv.x), abs(uv.y)) );
    color = mix(color, vec3(1), axes);

    float X = uv.x;
    float Y = func(X);


    float d = distance(uv, vec2(X, Y));
    float f = smoothstep(2. * pix_size, pix_size*.75, d);

    color = mix(color, vec3(1), f);


    // tangent check
    vec2 mousepoint = vec2(mouse.x, func(mouse.x));
    float m = distance(mousepoint, uv);
    float ma = smoothstep(5. * pix_size, pix_size*4., m);

    vec2 tangent = vec2(1., func_derivative(mouse.x));

    vec2 normal = normalize( vec2(1,-1) * tangent.yx );

    float tangent_dist = abs(dot((uv - mousepoint), normal));
    float tangent_alpha = smoothstep(1.5 * pix_size, pix_size*1., tangent_dist) * .5;
    tangent_alpha *= float(m < 1.);
    color = mix(color, vec3(0,.5,.5), tangent_alpha);




    color = mix(color, vec3(0,1,0), ma);


    float r = 5. * pix_size;
    int numsteps = 100;
    float x = mouse.x;
    float y = func(x);
    float dydx = func_derivative(x);
    float stepsize = 2./float(numsteps);
    for(int stp = 0; stp < numsteps; stp++)
    {
        float t = float(stp)/float(numsteps-1);

        float t_max = fract(iTime);

        if(t > t_max) break;

        vec3 particle_color = mix(vec3(0,0,1), vec3(0, 1, 0), t);
        //particle_color /= dot(particle_color, vec3(.5));
        particle_color /= length(particle_color);
        //particle_color = tanh(particle_color);

        vec2 pos = vec2(x, y);
        float dist = distance(uv, pos);
        float alpha = smoothstep(r+pix_size/2., r-pix_size/2., dist);
        color = mix(color, particle_color, alpha);

        x -= stepsize * dydx;

        y = func(x);
        dydx = func_derivative(x);
    }


    color = pow(color, vec3(1./2.2));
    fragColor = vec4(color, 1.);
}
