vec4 rectangle(vec2 uv, vec2 pos, float width, float height, vec3 color) {
	float t = 0.;
	if (uv.x >= pos.x && (uv.x < pos.x + width)
		&& (iResolution.y - uv.y) >= pos.y && ((iResolution.y - uv.y) < pos.y + height)) {
		t = 1.;
	}
	return vec4(color, t);
}

vec4 disc(vec2 uv, vec2 center, float radius, vec3 color) {
    float t = 0.;
    if (length(center-uv) < radius) {
        t = 1.;
    }
    return vec4(color, t);    
}

vec4 radial(vec2 uv, vec2 center, float r1, vec3 c1, float r2, vec3 c2, float r3, vec3 c3) {
    vec4 c = vec4(0.);  
    if (length(center-uv) < r1) {
        c = vec4(c1,1.);
    } else if (length(center-uv) < r2) {
        c = vec4(c2,1.);
    } else if (length(center-uv) < r3) {
        c = vec4(c3,1.);
    }
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 uv = fragCoord.xy;
    vec2 center = vec2(iResolution.x * .5, iResolution.y * .5);

	// colors
    vec3 lightblue = vec3(0.396,0.573,0.812);
    vec3 darkblue = vec3(0.141,0.239,0.514);
    vec3 yellow = vec3(0.933,0.722,0.314);

    // objects
    vec4 rect1 = rectangle(uv, vec2(iResolution.x * .1, iResolution.y * .15), iResolution.x * .8, iResolution.y * .7, darkblue);
    vec4 rad1 = radial(uv, center, iResolution.x * .07, yellow, iResolution.x * .2, darkblue, iResolution.x * .34, lightblue);

    // mix background & rect
	fragColor = mix(vec4(lightblue,1.0), rect1, rect1.a);
    fragColor = mix(fragColor, rad1, rad1.a);
}
