float rand(float n) {return fract(sin(n) * 43758.5453123);}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 mouse = iMouse.xy / iResolution.xy;
    float time = iTime;
    float ratio = iResolution.y / iResolution.x;
    vec2 uv = (2. * fragCoord - iResolution.xy) / iResolution.xy;
    fragColor = mix(vec4(0.,0.25,0.3, 1.), vec4(0.,0.,0.15, 1.), clamp(length(uv)*0.8,0.,1.));
    vec2 uv2 = (clamp(abs(uv),0.8, 1.)-0.8)*5.;
    float r = 0.0005 * mix(1., 0., max(uv2.x,uv2.y));
    uv.y *= ratio;
    float l = 0.;
    for (float i=0.; i<40.; i+=1.) {
        float r1 = rand(i);
        float r2 = rand(i*1.3);
    	vec2 p = mod(vec2(
            13.*r1 + 0.1*cos(r1*time + r1) - (mouse.x/floor(5.+i/10.)*2.),
            13.*r2 + 0.1*sin(r2*time + r2) - (mouse.y/floor(5.+i/10.)*2.)
        ),2.)-1.;
    	float d = distance(uv, p);
    	l += pow(r/d*(sin(time+i)+1.),1.5);
    }

    fragColor += vec4(vec3(0.,1.0,1.0)*l,1.0);
}
