void mainImage(out vec4 fragColor, in vec2 fragCoord) 
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = ( 2.* fragCoord - iResolution.xy ) / iResolution.y;

    // Time varying pixel color
	vec3 col = 0.2 + 0.2 * cos(iTime + uv.xyx + vec3(0, 2, 4));

    //old method: mod(uv * 1.5, 1.0) - 0.5
	vec2 p = fract(uv * 1.5) - 0.5;
	float a = atan(p.y, p.y); 
	float r = length(p) * 50.;

	col += smoothstep(1., .7, cos(r - iTime*3.0 + a * 0.01))*0.8; // (insert float) * (atan float) + number
	                                                              // align so the center isn't cut in half
    // Output to screen
	fragColor = vec4(col, 1);
}
