void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
	vec2 uv = fragCoord / iResolution.xx;

	float f = noise(20.0 * uv) * 0.5 + 0.5;

	fragColor = vec4(f);
}

vec3 hash2x3(vec2 p)
{
	vec3 p3 = fract(vec3(p.xyx) * vec3(0.1031, 0.1030, 0.0973));
    p3 += dot(p3, p3.yxz + 33.33);
    return fract((p3.xxy + p3.yzz) * p3.zyx);
}

float noise(in vec2 p)
{
    vec2 i = floor(p);
    vec2 f = fract(p);

	vec2 a = vec2(0.0);
    for(int y = -1; y <= 1; y++)
        for(int x = -1; x <= 1; x++)
        {
            vec2 g = vec2(x, y);
            vec3 o = hash2x3(i + g);
            vec2 d = g - f + mix(vec2(0.5), o.xy, 0.5);
            float w = max(1.0 - dot(d, d), 0.0);
            w *= w;
            vec2 grad = vec2(cos(o.z * 6.28318531), sin(o.z * 6.28318531));
            a += vec2(dot(d, grad) * w, w);
        }

    return a.x / a.y;
}
