//Update:it's no longer black intially and recommendations from Fabrice Neyret.
//234 chars

void mainImage( out vec4 c, in vec2 p )
{
    vec2 m = 1e2+iMouse.xy;
    float s = ceil(m.x/35.), r, h;
    p = floor(s*p/iResolution.y);
    p.y > p.x ? h = 1., p = p.yx :p;
    r = min(p.y, s-1.-p.x);
    c = fract(vec4(4.*(r+h)*(s-r-h) + sign(.5-h)*(p.x+p.y-r-r))/(s*s-1.)+iTime*3e2/m.y);
}

