void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 xy = fragCoord/iResolution.xy*vec2(2)-vec2(1);
    vec2 baseXY = xy;

    float fracttime = (iTime*2.5 - floor(iTime*2.5));
    float rottime = exp(fracttime)+exp(-fracttime);


    vec2 cur = vec2(0,0);
    float z = 0.0;
    int whites=0;
    for(int i = 0; i < 200; i++) {
        cur += xy*0.1;
        xy -= vec2(sin(iTime)*0.01,cos(iTime*3.0)*0.005+cos(z+iTime)*0.01);
        z += 0.05;
        if(length(vec2(cur.x/(2.0+sin(iTime*1.5)), cur.y/(2.0+cos(iTime*1.0+0.25)))) > 1.0) break;

        if(mod(z/20.0+iTime*0.15,1.0)>0.8) {
            whites+=1;
        }

    }

    vec4 col = texture(iChannel0,vec2(atan(xy.x,xy.y),z/20.0)+vec2(rottime/20.0+cos(z*0.05),iTime*0.15));

    fragColor = vec4(col.rgb,1)*vec4(0,sin(z+iTime*0.15)*0.25+0.25,1,1);

    fragColor.rgb = mix(fragColor.rgb,vec3(1),1.0-exp(-float(whites)/10.0));
}
