#define PI 3.14159
#define TWO_PI 6.28318

vec3 palette( in float t, in vec3 a, in vec3 b, in vec3 c, in vec3 d )
{
    return a + b*cos( TWO_PI*(c*t+d) );
}

vec2 Rotate2( in float a, in vec2 v )
{
    return vec2(
        v.x * cos(a) - v.y * sin(a),
        v.x * sin(a) + v.y * cos(a)
    );
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy + iTime * 0.25;
    uv = Rotate2(PI/4.0,uv);
    uv = vec2(uv.x,fract(uv.y * 8.0));
    uv = (uv - 0.5) * 2.0;
    float d = smoothstep(-1.0,1.0,cos(iTime/1.5))*2.0-1.0;
    float t = uv.y * d * 0.5 + 0.5;
    vec3 col = palette(t,
        vec3(0.5,0.5,0.5),
        vec3(0.5,0.5,0.5),
        vec3(1.0,1.0,1.0),
        vec3(0.0,0.1,0.2));
    fragColor = vec4(col,1.0);
}
