#define R iResolution
#define t -iTime / 8.
#define m  vec2( sin( t/2.  ) * cos( t /2. ) , cos( 2. * sin( t ) ) ) *.2;
#define lng(x) length(x)
#define rot(a) mat2(cos(a),-sin(a),sin(a),cos(a))

vec3 bgr( vec2 i ){

  return vec3( mod( ceil( i / 9. ).x - ceil( i / 8.  ).y, 2. ) );
}

vec3 ball(vec2 i){
    float r = lng(i) / .079;
    i *= sqrt(r*8.)*2.;
    vec2 ii = vec2(r,atan(i.x,i.y)-t/.15)+2.9*t;
    ii *= rot(12.);
   return bgr(ii*56.) + (1.35 -  r) - lng(i/3.)-.3;
}

void mainImage( out vec4 o, in vec2 i ){
   i = ( i - .5 * R.xy ) / R.y - m;
   i*=rot(122.*t/221.);
   float b = 1.-smoothstep(.02,.5, lng(i));
   vec2 st = vec2(atan(i.x,i.y) , lng(i)-t/3.)-t/4.;
   st *= rot(112.);
   o.rgb = max( bgr(st*295.)-b/.9 , ball( i ) / 1.03 );
}
