// For correct ambient occlusion sample 2x2x2 voxels (slow!)
//#define HIGH_QUALITY XXX CURRENTLY BROKEN DUE TO APPROXIMATE SDF AT PORTALS XXX

float hash12(vec2 p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    vec3 p3  = fract(vec3(p.xyx) * .1031);
    p3 += dot(p3, p3.yzx + 33.33);
    return fract((p3.x + p3.y) * p3.z);
}

vec2 hash22(vec2 p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yzx+33.33);
    return fract((p3.xx+p3.yz)*p3.zy);
}

vec3 hash32(vec2 p) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    vec3 p3 = fract(vec3(p.xyx) * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy+p3.yzz)*p3.zyx);
}

vec3 hash33(vec3 p3) // https://www.shadertoy.com/view/4djSRW Hash without Sine by Dave_Hoskins
{
    p3 = fract(p3 * vec3(.1031, .1030, .0973));
    p3 += dot(p3, p3.yxz+33.33);
    return fract((p3.xxy + p3.yxx)*p3.zyx);
}

struct block
{
    vec4 center;
    vec4 extent;
    ivec2 id;
    ivec2 id0;
    ivec2 id1;
    bool flag;
};

block empty_block()
{
    return block(vec4(0), vec4(0), ivec2(0), ivec2(0), ivec2(0), false);
}

#define block_offset 0.2
#define wall_size 0.02

float block_hash(ivec2 t, int x, int y, bool choose, bool mirror)
{
    ivec2 d = (mirror?ivec2(y, x):ivec2(x, y));
    //vec2 r = fract(hash22(vec2(t+d)))+vec2(d); // [0,1]
    vec2 r = mix(vec2(block_offset), vec2(1.0 - block_offset), fract(hash22(vec2(t+d))))+vec2(d); // [constrained]
    return choose?r.y:r.x;
}

// Inspired by https://www.shadertoy.com/view/Ws3GRs Asymmetric Blocks by Shane,
// and also by https://www.shadertoy.com/view/tsVGRz Oddtiles (optimized) by stb.
// Checkerboard tiling of alternating (horizontal vs. vertical) (double-T-) crossings.
// Returns minimum, maximum, id.
// Compactified using implicit matrix transpose & deferred hash calls.
//(NOTE: inlining the hash-function and using out parameters instead of block structure
//       could be used to turn this into a stand-alone function.)
block blocks_layout(ivec2 tile, vec2 local) // https://www.shadertoy.com/view/dstfzf asymmetric blocks compactified f by jt
{
    //bool flip = (tile.x % 2) != (tile.y % 2); // XXX breaks on windows XXX
    bool flip = (tile.x & 1) != (tile.y & 1); // alternate direction of double T crossings: vertical or horizontal
    //flip = !flip; // "dual" pattern (i.e. T-crossings flipped)

    // layout (NOTE: matrix notation upside down compared to coordinate system!)
    // NW N NE
    //  W C E
    // SW S SE

    // transposed (NOTE: matrix notation upside down compared to coordinate system!)
    // SE W NE
    //  N C S
    // SW E NW

    if(flip) // horizontal line goes through: swap vertical with horizontal
    {
        // swap components
        local = local.yx;
    }

    // vertical line goes through (on flip transpose for horizontal line)

    //                 c
    //   +-+---------+---+-------+
    //   | |         |   |       |
    //   | |         |   #-------+ d
    // d +-#-#-------+---M       |
    //   |   |       |   |       |
    //   +---+-------+---+-------+
    //   |   |       |   |       |
    //   |   m-------+---#---#---+
    //   +---#       |       |   |
    //   |   |       |       |   |
    //   +---+-------+-------+---+

    float c = block_hash(tile, 0, 0,  flip, flip); // offset of central line
    int x = 1-int(local.x < c); // [0,1]
    int X = 2*x-1; // [-1,+1]
    float d = block_hash(tile,+X, 0, !flip, flip); // offset of branching line
    float e = block_hash(tile,-X, 0,!flip, flip); // offset of branching line
    int y = 1-int(local.y < d); // [0,1]
    int Y = 2*y-1; // [-1,+1]

    vec4 v0 =
        vec4
        (
            // primary corner in (0,0) tile
            c,
            d,
            // secondary corner in (0,0) tile
            c,
            e
        );

    vec4 v1 =
        vec4
        (
            // primary corner in (X,Y) tile
            block_hash(tile,X,Y, flip, flip),
            block_hash(tile,0,Y,!flip, flip),
            // secondary corner in (X,Y) tile
            block_hash(tile,2*X,Y, flip, flip),
            block_hash(tile,0,Y,!flip, flip)
        );

    if(flip) tile = tile.yx;

    v0 += vec2(tile).xyxy;
    v1 += vec2(tile).xyxy;

    ivec2 id = tile + ivec2(x,y);
    ivec2 id0 = tile + ivec2(1-x,1-int(local.y < e));
    ivec2 id1 = tile + ivec2(1-x,1-int(local.y < e));

    if(flip) // horizontal line goes through: unswap vertical with horizontal
    {
        v0 = v0.yxwz;
        v1 = v1.yxwz;
        id = id.yx;
        id0 = id0.yx;
        id1 = id1.yx;
    }

    bool flag = v0.y > v1.y;

    // using abs in conversion to center-extend fixes order of min/max corners
    return block((v0+v1)/2.0, abs(v0-v1)/2.0, id, id0, id1, flag);
}

#define pi 3.1415926

mat3 yaw_pitch_roll(float yaw, float pitch, float roll)
{
    mat3 R = mat3(vec3(cos(yaw), sin(yaw), 0.0), vec3(-sin(yaw), cos(yaw), 0.0), vec3(0.0, 0.0, 1.0));
    mat3 S = mat3(vec3(1.0, 0.0, 0.0), vec3(0.0, cos(pitch), sin(pitch)), vec3(0.0, -sin(pitch), cos(pitch)));
    mat3 T = mat3(vec3(cos(roll), 0.0, sin(roll)), vec3(0.0, 1.0, 0.0), vec3(-sin(roll), 0.0, cos(roll)));

    return R * S * T;
}

float halfspace(vec3 p)
{
    return p.z + 1.0;
}

float sphere(vec3 p, float r)
{
    return length(p) - r;
}

float approx_crossing(vec3 p, float r)
{//return sphere(p, r);
    return max(length(p.xz)-r, length(p.yz)-r);
}

float box(vec2 p, vec2 s) // adapted from https://iquilezles.org/articles/distfunctions/
{
    vec2 d = abs(p) - s;
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float box(vec3 p, vec3 s) // adapted from https://iquilezles.org/articles/distfunctions/
{
    vec3 d = abs(p) - s;
    return min(max(d.x,max(d.y,d.z)),0.0) + length(max(d,0.0));
}

float box(vec3 p, vec3 p0, vec3 p1)
{
    return box(p-(p1+p0)/2.0, abs(p1-p0)/2.0);
}

float box(vec2 p, vec2 p0, vec2 p1, float r) // min-max box with rounded corner
{
    return box(p-(p1+p0)/2.0, abs(p1-p0)/2.0-r)-r;
}

float box(vec3 p, vec3 p0, vec3 p1, float r) // min-max box with rounded corner
{
    float d = box(p.xy, p0.xy, p1.xy, r);
    vec2 w = vec2(d, abs(p.z-(p1.z+p0.z)/2.0) - (p1.z-p0.z)/2.0);
    return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}

float dot2( in vec2 v ) { return dot(v,v); }

float cone(vec3 p, float h, float r1, float r2) // adapted from https://iquilezles.org/articles/distfunctions/
{
    vec2 q = vec2(length(p.xy), p.z);
    vec2 k1 = vec2(r2,h);
    vec2 k2 = vec2(r2-r1,2.0*h);
    vec2 ca = vec2(q.x-min(q.x,(q.y<0.0)?r1:r2), abs(q.y)-h);
    vec2 cb = q - k1 + k2*clamp( dot(k1-q,k2)/dot2(k2), 0.0, 1.0 );
    float s = (cb.x<0.0 && ca.y<0.0) ? -1.0 : 1.0;
    return s*sqrt( min(dot2(ca),dot2(cb)) );
}

/*
float cylinder(vec3 p, vec2 s) // adapted from https://iquilezles.org/articles/distfunctions/
{
    vec2 d = abs(vec2(length(p.xy),p.z)) - s;
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}
*/
float cylinder(vec3 p, float r, float h) // adapted from https://iquilezles.org/articles/distfunctions/
{
    vec2 d = abs(vec2(length(p.xy),p.z)) - vec2(r,h);
    return min(max(d.x,d.y),0.0) + length(max(d,0.0));
}

float frame(vec2 p, vec2 s, float t)
{
    return max(box(p, s),-box(p, s-t));
}

float frame(vec3 p, vec3 s, float t)
{
    float d = frame(p.xy, s.xy, t);
    // extrude https://iquilezles.org/articles/distfunctions/
    vec2 w = vec2(d, abs(p.z) - s.z);
    return min(max(w.x,w.y),0.0) + length(max(w,0.0));
}

float vmax(vec2 v)
{
    return max(v.x, v.y);
}

void pR45(inout vec2 p)
{
    p = (p + vec2(p.y, -p.x))*sqrt(0.5);
}

float pMod1(inout float p, float size)
{
    float halfsize = size*0.5;
    float c = floor((p + halfsize)/size);
    p = mod(p + halfsize, size) - halfsize;
    return c;
}

// https://www.shadertoy.com/view/Xs3GRB HG SDF in WebGL by tomkh,
// implementation of http://mercury.sexy/hg_sdf/
float fOpUnionStairs(float a, float b, float r, float n)
{
    float d = min(a, b);
    vec2 p = vec2(a, b);
    pR45(p);
    p = p.yx - vec2((r-r/n)*0.5*sqrt(2.));
    p.x += 0.5*sqrt(2.)*r/n;
    float x = r*sqrt(2.)/n;
    pMod1(p.x, x);
    d = min(d, p.y);
    pR45(p);
    return min(d, vmax(p -vec2(0.5*r/n)));
}

float make_cylinder_grid(vec3 l, float r)
{
    float d =
        min
        (
            min(length(l.xz-vec2(1.0,0))-r, length(l.xz-vec2(0.0,0))-r),
            min(length(l.yz-vec2(1.0,0))-r, length(l.yz-vec2(0.0,0))-r)
        );
    return d;
}

float make_cuboid_grid(vec3 l, float r, float s)
{
    float d =
        min
        (
            min(box(l.xz-vec2(1.0,0),vec2(r, s)), box(l.xz-vec2(0.0,0),vec2(r, s))),
            min(box(l.yz-vec2(1.0,0),vec2(r, s)), box(l.yz-vec2(0.0,0),vec2(r, s)))
        );
    return d;
}

#define EPSILON 0.001
#define DIST_MAX 50.0
#define ITER_MAX 200u

struct result
{
    float dist;
    vec4 color_specular;
    bool flag;
};

result combine(result a, result b)
{
    if(a.dist < b.dist) return a;
    return b;
}

result remove(result a, result b)
{
    if(a.dist > -b.dist) return a;
    return result(-b.dist, b.color_specular, b.flag);
}

vec3 colorize(ivec2 id)
{
    vec3 color = hash32(vec2(id));
    if(((id.x^id.y)&1)==0)
        color = 1.0 - color;

    vec3 n = vec3(1,1,1);
    color -= 0.5*dot(color,n)*n/dot(n,n); // remove part of component along diagonal

    return color;
}

result map(ivec2 v, vec3 l) // unit grid: voxel = float(position), local = fract(position)
{
    result res = result(DIST_MAX, vec4(vec3(1), 0), false);

    block b = blocks_layout(v.xy, l.xy);
    float h = 0.2;
    //float h = 0.2+0.1 * hash12(vec2(b.id));
    //float h0 = 0.1 * hash12(vec2(b.id0));
    //float h1 = 0.1 * hash12(vec2(b.id1));
    res = combine(res, result(frame(vec3(v,0)+l-vec3(b.center.xy,0), vec3(b.extent.xy-wall_size,h), wall_size), vec4(colorize(b.id), 0), false));
    res = combine(res, result(frame(vec3(v,0)+l-vec3(b.center.xy,0), vec3(b.extent.xy,h/* *0.9*/), wall_size), vec4(vec3(1), 0), false));

    vec2 v0 = b.center.xy - b.extent.xy - vec2(v);
    vec2 v1 = b.center.xy + b.extent.xy - vec2(v);
    vec2 w0 = b.center.zw - b.extent.zw - vec2(v);
    vec2 w1 = b.center.zw + b.extent.zw - vec2(v);

    //res = combine(res, result(cylinder(vec3(v,0)+l-vec3(b.center.xy,0), 0.25*min(b.extent.x, b.extent.y), h), vec4(vec3(1),0), false));
    //res = combine(res, result(cylinder(vec3(v,0)+l-vec3(b.center.xy,0), 0.25*min(b.extent.x, b.extent.y)+0.05, 0.01), vec4(vec3(0.25),0), false));

/*
    float cyl =
        min
        (
            min
            (
                cylinder(l-vec3(v0.xy,0), h, 0.1),
                cylinder(l-vec3(v1.xy,0), h, 0.1)
            ),
            min
            (
                cylinder(l-vec3(v0.x, v1.y,0), h, 0.1),
                cylinder(l-vec3(v1.x, v0.y,0), h, 0.1)
            )
        );
    res.dist = min(res.dist, cyl);
*/
    if(!b.flag)
    //if(true)
    {
        {
            vec3 c = vec3(v1.x+w1.x,v0.y+w0.y,0/*+h*/)/2.0;
            vec3 e = abs(vec3(v1.x-w1.x,v0.y-w0.y,-h))/2.0 - wall_size;

            //res = combine(res, result(box(l, vec3(v1.x+2.0*wall_size,v0.y+wall_size,0), vec3(w1.x-2.0*wall_size, w0.y-wall_size, 0.25)), colorize(b.id0)));
            //res = combine(res, result(sphere(l-c, max(e.x,e.y)), colorize(b.id0)));
            // remove wall at portal
            //res.dist = max(res.dist,-box(l, vec3(v1.x+2.0*wall_size,v0.y+wall_size,0), vec3(w1.x-2.0*wall_size, w0.y-wall_size, 0.25)));
            res = remove(res, result(approx_crossing(l-c, max(e.x,e.y)-wall_size), vec4(vec3(1),0), false));
        }
        {
            vec3 c = vec3(v0.x+w0.x,v0.y+w0.y,0/*+h*/)/2.0;
            vec3 e = abs(vec3(v0.x-w0.x,v0.y-w0.y,-h))/2.0 - wall_size;

            //res = combine(res, result(box(l, vec3(v1.x+2.0*wall_size,v0.y+wall_size,0), vec3(w1.x-2.0*wall_size, w0.y-wall_size, 0.25)), colorize(b.id0)));
            //res = combine(res, result(sphere(l-c, max(e.x,e.y)), colorize(b.id0)));
            // remove wall at portal
            //res.dist = max(res.dist,-box(l, vec3(v1.x+2.0*wall_size,v0.y+wall_size,0), vec3(w1.x-2.0*wall_size, w0.y-wall_size, 0.25)));
            res = remove(res, result(approx_crossing(l-c, max(e.x,e.y)-wall_size), vec4(vec3(1),0), false));
        }
    }
    else
    {
        {
            vec3 c = vec3(v0.x+w0.x,v1.y+w1.y,0/*+h*/)/2.0;
            vec3 e = abs(vec3(v0.x-w0.x,v1.y-w1.y,-h))/2.0 - wall_size;

            //res = combine(res, result(box(l, vec3(v0.x-2.0*wall_size,v1.y-wall_size,0), vec3(w0.x+2.0*wall_size, w1.y+wall_size, 0.25)), colorize(b.id0)));
            // remove wall at portal
            //res.dist = max(res.dist,-box(l, vec3(v0.x-2.0*wall_size,v1.y-wall_size,0), vec3(w0.x+2.0*wall_size, w1.y+wall_size, 0.25)));
            res = remove(res, result(approx_crossing(l-c, max(e.x,e.y)-wall_size), vec4(vec3(1),0), false));
        }
        {
            vec3 c = vec3(v1.x+w1.x,v1.y+w1.y,0/*+h*/)/2.0;
            vec3 e = abs(vec3(v1.x-w1.x,v1.y-w1.y,-h))/2.0 - wall_size;

            //res = combine(res, result(box(l, vec3(v0.x-2.0*wall_size,v1.y-wall_size,0), vec3(w0.x+2.0*wall_size, w1.y+wall_size, 0.25)), colorize(b.id0)));
            // remove wall at portal
            //res.dist = max(res.dist,-box(l, vec3(v0.x-2.0*wall_size,v1.y-wall_size,0), vec3(w0.x+2.0*wall_size, w1.y+wall_size, 0.25)));
            res = remove(res, result(approx_crossing(l-c, max(e.x,e.y)-wall_size), vec4(vec3(1),0), false));
        }
    }

    //res = remove(res, result(make_cylinder_grid(l-vec3(0,0,h), 0.1), vec4(vec3(1),0), false));
    //res = remove(res, result(make_cylinder_grid(l-vec3(0,0,h), 0.1), vec4(vec3(1),0), false));
    //res = combine(res, result(make_cuboid_grid(l-vec3(0,0,h*0.6), 0.05, 0.005), vec4(vec3(0.25),0), false));

    //res = combine(res, result(l.z, vec3(1))); // floor

    float f = l.z; // floor
    float cone_south = cone(vec3(v,0)+l-vec3(b.center.x,b.center.y-b.extent.y,0.0), h-0.0001, .0, h/4.0);
    float cone_north = cone(vec3(v,0)+l-vec3(b.center.x,b.center.y+b.extent.y,0.0), h-0.0001, .0, h/4.0);
    float cone_west = cone(vec3(v,0)+l-vec3(b.center.x-b.extent.x,b.center.y,0.0), h-0.0001, .0, h/4.0);
    float cone_east = cone(vec3(v,0)+l-vec3(b.center.x+b.extent.x,b.center.y,0.0), h-0.0001, .0, h/4.0);
    //res.dist = min(res.dist, cone0);
    f = fOpUnionStairs(f, min(min(cone_south, cone_east), min(cone_north, cone_west)), h, 10.0);

    res = combine(res, result(f, vec4(1.-colorize(b.id), 1), true)); // floor

    return res;
}
#ifdef HIGH_QUALITY
result map(vec3 p) // for correct ambient occlusion sample 2x2x2 voxels (slow!)
{
    // I think kastorp originally suggested to sample only 2x2x2 instead of 3x3x3, thanks!
    result d = result(DIST_MAX, vec4(vec3(0),0), false);
    ivec2 s = ivec2(step(0.5, fract(p.xy)));
    ivec2 o;
    for(o.y = s.y-1; o.y < s.y+1; o.y++)
        for(o.x = s.x-1; o.x < s.x+1; o.x++)
        {
            result r = map(ivec2(floor(p.xy))+o, vec3(fract(p.xy), p.z)-vec3(o,0));
            if(r.dist < d.dist)
                d = r;
        }
    return d;
}
#else
result map(vec3 p)
{
    return map(ivec2(floor(p.xy)), vec3(fract(p.xy), p.z));
}
#endif
// https://iquilezles.org/articles/normalsSDF tetrahedron normals
vec3 normal(vec3 p)
{
    const float h = EPSILON;
    const vec2 k = vec2(1,-1);
    return normalize(k.xyy*map(p + k.xyy*h).dist +
                     k.yyx*map(p + k.yyx*h).dist +
                     k.yxy*map(p + k.yxy*h).dist +
                     k.xxx*map(p + k.xxx*h).dist);
}

// NOTE: Apparently sign fails on some systems! Thanks to spalmer for debugging this!
vec2 sgn(vec2 v) // WORKAROUND FOR COMPILER ERROR on some systems
{
    return step(vec2(0), v) * 2.0 - 1.0;
}

result trace(vec3 ro, vec3 rd, float t0, float t1) // ray-march sdf handling discontinuities between voxels  (jt)
{
    result h;
    uint i;
    float t;
    for(t = t0, i = 0u; t < t1 && i < ITER_MAX; i++) // finite loop originally suggested by pyBlob to avoid stalling if ray parallel to surface just above EPSILON
    {
        vec3 p = ro + rd * t;
        h = map(p);
        if(h.dist < EPSILON)
            return result(t, h.color_specular, h.flag);

        // NOTE: An extra step per block, use if sdf discontinuous between blocks
        //       Could make this conditional by prefixing sth. like if(block_changed) // suggested by spalmer
        // constrain step to blocks (voxel-snap ray-march plugin by jt, thanks to Shane for the idea!)
        {
            block b = blocks_layout(ivec2(floor(p.xy)), fract(p.xy));
            // NOTE: assuming unit grid
            // sgn(rd)*extent are the block walls in ray direction, fract(p) - center is position relative to center of block
            //vec2 sd = (sgn(rd.xy)*b.extent.xy - (fract(p.xy) - (b.center.xy-floor(p.xy))))/rd.xy; // distances to block sides / walls
            vec2 sd = (sgn(rd.xy)*b.extent.xy - p.xy + b.center.xy)/rd.xy; // distances to block sides / walls
            vec2 n = step(sd.xy, sd.yx); // component true if corresponding wall is nearest (at most one component true) NOTE: originally I used lessThanEqual, min from fb39ca4/kzy then switched to step, min by iq
            float skip = dot(sd, n) + EPSILON; // distance to next block: sum up all components, weighted by the nearest flag (assuming only one component is true this selects the nearest component)
            h.dist = min(h.dist, skip); // constrain step to at most next block to handle sdf discontinuities between voxels
        }

        t += h.dist;
    }

    return result(t, h.color_specular, h.flag); // stop on running out of iterations
    //return result(t1, vec4(0), false); // pass on running out of iterations
}

// NOTE: Don't forget to add +normal*EPSILON to the starting position
//       to avoid artifacts caused by getting stuck in the surface
//       due to starting at distance < EPSILON from the surface.
//       (normal could be calculated here but that would most likely be redundant)
//       Thanks to spalmer for pointing that out.
float shadow(vec3 ro, vec3 rd, float t0, float t1)
{
    return trace(ro, rd, t0, t1).dist < t1 ? 0.0 : 1.0;
}

// https://iquilezles.org/articles/rmshadows
float softshadow(vec3 ro, in vec3 rd, float t0, float t1, float k)
{
    float res = 1.0;
    float ph = 1e20;
    uint i;
    float t;
    for(t = t0, i = 0u; t < t1 && i < ITER_MAX; i++)
    {
        float h = map(ro + rd*t).dist;
        if( h < EPSILON )
            return 0.0;
        float y = h*h/(2.0*ph);
        float d = sqrt(h*h-y*y);
        res = min( res, k*d/max(0.0,t-y) );
        ph = h;
        t += h;
    }
    return res;
}

// https://www.shadertoy.com/view/Xds3zN raymarching primitives
float calcAO( in vec3 pos, in vec3 nor )
{
    float occ = 0.0;
    float sca = 1.0;
    for( int i=0; i<5; i++ )
    {
        float h = 0.01 + 0.12*float(i)/4.0;
        float d = map( pos + h*nor ).dist;
        occ += (h-d)*sca;
        sca *= 0.95;
        if( occ>0.35 ) break;
    }
    return clamp( 1.0 - 3.0*occ, 0.0, 1.0 ) ;
}

float checker(vec2 p)
{
    return step(0.0, sin(p.x*pi)*sin(p.y*pi));
}

float checker(vec3 p)
{
    return step(0.0, sin(p.x*pi)*sin(p.y*pi)*sin(p.z*pi));
}

vec3 material(vec3 p)
{
    //return vec3(1.0-pow(0.5+0.5*sin(p.z*pi*10.0), 50.0));
    return vec3(1.0);
    //return mix(vec3(0.25), vec3(0.75), checker(p*2.0+0.5));
}

vec3 sky(vec3 lightdir, vec3 d)
{
    float v = 0.5*abs(d.z)+0.5;
    return vec3(pow(1.0-v,2.0),1.0-v,v); // no particular meaning - just tweaked until it looks skyish
}

float sun(vec3 lightdir, vec3 rd)
{
    float d = max(0.0, dot(rd, lightdir));
    return smoothstep(0.998, 0.999, d) + exp(-pow((1.0-d)*500.0, 2.0)); // sun with halo
}

vec3 pass(vec3 ro, vec3 rd, result r)
{
    vec3 lightdir = normalize(vec3(3.0, 2.0, 1.0));

    //vec3 sky_color = mix(vec3(0.2,0.5,0.5),vec3(0,0,1),abs(-rd.z));
    //vec3 sky_color = mix(vec3(0.2,0.5,0.5),vec3(0,0,0.5),sqrt(abs(rd.z)));
    vec3 sky_color = sky(lightdir, rd);
    sky_color += sun(lightdir, rd);

    vec3 color = vec3(1);
    if(r.dist < DIST_MAX)
    {
        color *= r.color_specular.xyz;
        vec3 dst = ro + rd * r.dist;
        vec3 n = normal(dst);

        //color *= (n * 0.5 + 0.5);
        //color *= material(dst);

        vec3 ambient = vec3(0.4);
        float brightness = max(dot(lightdir, n), 0.0);
        if(brightness > 0.0)
            brightness *= shadow(ro + rd * r.dist + n * 0.01, lightdir, 0.0, DIST_MAX);
            //brightness *= softshadow(ro + rd * dist + n * 0.01, lightdir, 0.0, DIST_MAX, 20.0);
        color *= (ambient * calcAO(dst, n) + brightness);

        if(brightness > 0.0)
        {
            float specular = pow(max(0.0, dot(n, normalize(-rd + lightdir))), 250.0);
            color += specular * r.color_specular.w;
        }

        vec3 fog_color = sky_color;
        color = mix(fog_color, vec3(color), exp(-pow(r.dist/20.0, 2.0))); // fog
    }
    else
    {
        color *= sky_color;
    }

    return color;
}

float grey(vec3 color)
{
    return dot(color, vec3(1.0/3.0));
}

void mainImage(out vec4 fragColor, vec2 I)
{
    bool demo = all(lessThan(iMouse.xy, vec2(10.0)));
    vec2 R = iResolution.xy;
    I = (2.0 * I - R) / R.y; // concise scaling thanks to Fabrice
    //I = (4.0 * floor(I * 0.5) + 2.0 - R) / R.y; // integrated pixelize (speedup & looks cool) thanks to spalmer

    float yaw = 2.0 * pi * float(iMouse.x) / float(R.x);
    float pitch = pi - pi / 2.0 * float(iMouse.y) / float(R.y);
    yaw = !demo ? yaw : 2.0 * pi * fract(iTime * 0.01);
    pitch = !demo ? pitch : 4.0/3.0 * pi / 2.0;

    vec3 ro = vec3(0.0, 0.0,-2.5);
    vec3 rd = normalize(vec3(I.xy, 2.0));

    mat3 M = yaw_pitch_roll(yaw, pitch, 0.0);
    ro = M * ro;
    rd = M * rd;
    ro.z += 0.0;

    result res = trace(ro, rd, 0.0, DIST_MAX);
    vec3 color = pass(ro, rd, res);
    if(res.dist < DIST_MAX && res.flag)
    {
        //color = vec3(1);
        // reflect camera position
        ro.z = -ro.z;
        // reflect view direction
        rd.z = -rd.z;

        res = trace(ro, rd, res.dist+0.01, DIST_MAX);
        vec3 reflection = pass(ro, rd, res);
        //color *= reflection; // clear
        //color = mix(color, reflection, 0.5); // muddy (light)
        color *= mix(vec3(1), reflection, 0.5); // muddy (dark)
        //color += reflection; // additive
        //color *= grey(pass(ro, rd, res)); // unrealistic
    }

    color = tanh(color); // roll-off overly bright colors

    fragColor = vec4(color, 1);
    fragColor = sqrt(fragColor); // approximate gamma
}
