float square(vec2 uv, vec2 center, float radius) {
    vec2 d = uv - center;       
    return radius - max(abs(d.x), abs(d.y));
}

float disc(vec2 uv, vec2 center, float radius) {
    return radius - length(uv-center);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x/iResolution.y;
    float ss = 1.5/iResolution.x;

    vec3 bg = vec3(0.043,0.14,0.16);
    vec3 c1 = vec3(0.95,0.68,0.24);
    vec3 c2 = vec3(0.6,0.51,0.21);

    vec3 col = bg;
    col = mix(col, c1, smoothstep(-ss,ss, disc(uv, vec2(.2,.2), .2)));
    col = mix(col, c2, smoothstep(-ss,ss, max(disc(uv, vec2(.0,.0), .2), square(uv, vec2(.1,.1), .1))));
    col = mix(col, c1, smoothstep(-ss,ss, max(disc(uv, vec2(-.2,-.2), .2), square(uv, vec2(-.1,-.1), .1))));

    fragColor = vec4(col,1.0);
}
