void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (2.0*fragCoord-iResolution.xy)/iResolution.y;

    float scaledTime=iTime*sqrt(iTime)*4.0;

    // https://www.desmos.com/calculator/ct8bonmqt6
    float d1 =
         (pow(abs(uv.x),4.)+pow(abs(uv.y),4.))
        -(pow(abs(uv.x),2.)-pow(abs(uv.y),2.))
        -0.1*sin(scaledTime*uv.x)*cos(scaledTime*uv.y)
        ;

    d1 = abs(d1);

    float eps1 = 0.01;
    float eps2 = 0.03;

    float smoothFactor = smoothstep(eps1, eps2, d1);

    vec3 baseColor = vec3(0.4627, 0.051, 0.051);
    vec3 transitionColor = vec3(1, 0.051, 0.051);

    vec3 finalColor = mix(baseColor,transitionColor, smoothFactor);

    if(d1 < eps2) fragColor = vec4(finalColor, 1.0);
    else fragColor = vec4(0.);
}
