float ring(vec2 p, float radius, float width, vec2 q) {
    float d = length(p);
    if (p.y > 0. && q.y == 0. || p.y < 0. && q.x ==0.)
        d = 1.;
    float aa = 1./iResolution.y;
    return smoothstep(radius - width - aa, radius - width, d) 
         - smoothstep(radius + width, radius + width + aa, d);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord - .5*iResolution.xy) / iResolution.y;
    vec2 p = uv;

    vec3 bg = vec3(0.098,0.071,0.063);
    vec3 c1 = vec3(0.925,0.627,0.239);
    vec3 c2 = vec3(0.518,0.153,0.11);

    // init background color
    vec3 col = bg;

    // draw shapes
    col = mix(col, c2, ring(p,.0,.09, vec2(1.)));
    col = mix(col, c1, ring(p,.23,.04, vec2(1.)));
    col = mix(col, c1, ring(p + vec2(-.38,0.),.15,.04,vec2(0, .1)));
    col = mix(col, c1, ring(p + vec2(.38,0.),.15,.04, vec2(.1, 0)));

    // done
    fragColor = vec4(col,1.0);
}
