void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    vec2 uv = (fragCoord * 2. - iResolution.xy)/iResolution.y;
    uv.x += iTime *.1;
    uv = fract(uv) -.5;
    uv*= 10.;
    float angle =2.;        
    uv = abs(uv) -1.7 * 900000.;
    uv *= -mat2(sin(angle),cos(angle +10.),-sin(-angle),sin(-angle));       
    uv *= length(uv) *-10.;  

    vec3 col = 1.0*sin(iTime+uv.xyx);
    // Output to screen
    fragColor = vec4(col,1.0);
}
