#define NB_PLAN 8
#define NB_SPHERE 1
#define NB_CYL 1
#define NB_CONE 1
#define NB_LUM 3

mat3 rotateX(float angle) {
    float c = cos(angle);
    float s = sin(angle);
    return mat3(
        1.0, 0.0, 0.0,
        0.0, c, -s,
        0.0, s, c
    );
}

mat3 rotateY(float angle) {
    float c = cos(angle);
    float s = sin(angle);
    return mat3(
        c, 0.0, s,
        0.0, 1.0, 0.0,
        -s, 0.0, c
    );
}

mat3 rotateZ(float angle) {
    float c = cos(angle);
    float s = sin(angle);
    return mat3(
        c, -s, 0.0,
        s, c, 0.0,
        0.0, 0.0, 1.0
    );
}

struct Camera {
    vec3 Obs;
    vec3 View;
    vec3 Up;
    vec3 Horiz;
    float H;
    float W;
    float z;
};

struct Ray {
    vec3 Origin;
    vec3 Dir;
};

struct Lum {
    vec3 Color;
    vec3 Pos;
    float I;
};

struct Plan {
    vec3 Dir1;
    vec3 Dir2;
    vec3 Color;
    vec3 Center;
    float h;
    float w;
};

struct Sphere {
   vec3 Center;
   float R;
   vec3 Color;
};

struct Cylinder {
   vec3 Center;
   vec3 Dir;  
   vec3 Color;
   float r;
   float h;
};

struct Cone {
    vec3 Center;
    vec3 Dir;
    vec3 Color;
    float r;
    float s;
    float h;
};

Camera camera(in vec3 Obs, in vec3 LookAt, in float aperture) {
    Camera C;
    vec3 vertical = vec3(0.0,0.0,1.0);
    C.View = (LookAt - Obs) / length(LookAt - Obs);
    C.Horiz = normalize(cross(vertical, C.View));
    C.Up = normalize(cross(C.View,C.Horiz));
    C.Obs = Obs;
    C.H = iResolution.y;
    C.W = iResolution.x;
    C.z = C.H/2.0/tan(aperture * 3.1415 / 180.0 /2.0);
    return C;
}

Ray launch(in Camera C, in vec2 XY) {
   Ray r;
   r.Origin = C.Obs;
   r.Dir = C.View * C.z + (XY.x - 0.5 * C.W) * C.Horiz + (XY.y - 0.5 * C.H) * C.Up;
   return r;
}

Lum lum(in vec3 Color, in vec3 Pos, in float I) {
    Lum L;
    L.Color = Color;
    L.Pos = Pos;
    L.I = I;
    return L;
}

Plan plan(vec3 center, vec3 d1, vec3 d2, vec3 color, float h, float w) {
    Plan P;
    P.Dir1 = d1;
    P.Dir2 = d2;
    P.Center = center;
    P.Color = color;
    P.h = h;
    P.w = w;
    return P;
}

Cylinder cylinder(vec3 center, vec3 d, vec3 color, float r, float h) {
    Cylinder C;
    C.Center = center;
    C.Dir = d;
    C.Color = color;
    C.r = r;
    C.h = h;
    return C;
}

Cone cone(vec3 center, vec3 d, vec3 color, float r, float s, float h) {
    Cone C;
    C.Center = center;
    C.Dir = d;
    C.Color = color;
    C.r = r;
    C.s = s;
    if (h>s) {h=s;}
    C.h = h;
    return C;
}

