bool intersect_sphere(in Ray R, in Sphere S, out float t) {

    float a = dot(R.Dir,R.Dir);
    float b = 2.0*dot(R.Dir,(R.Origin - S.Center));
    float c = dot(R.Origin - S.Center, R.Origin - S.Center) - S.R*S.R;
    float delta = b*b - 4.0*a*c;   

    if (delta > 0.0) {
        float t1 = (-b - sqrt(delta)) / (2.0*a);
        float t2 = (-b + sqrt(delta)) / (2.0*a);
        t = t1;
        if (t1 < 0.0) {
            t = t2;
            if (t2 < 0.0) {return false;}
        }

    } else if (delta == 0.0) {
        t = (-b - sqrt(delta)) / (2.0*a);
        if (t < 0.0) {return false;}
    } else {
        t = -1.0;
        return false;
    }
    return true;
}

bool intersect_plan(in Ray R, in Plan P, out float t) {
    vec3 n = cross(P.Dir1,P.Dir2);
    if (dot(n,R.Dir)!=0.0) {
        mat3 X = mat3(R.Dir,-P.Dir1,-P.Dir2);
        mat3 Xinv = inverse(X);
        vec3 V0 = P.Center - R.Origin;
        vec3 C = Xinv*V0;
        t = C.x;
        float u = C.y;
        float v = C.z;

        if ( ( P.h > 0.0 && P.h < abs(u) )||  ( P.w > 0.0 && P.w < abs(v) ) )  {
            t = -1.0;
            return false;
        }
        if (t > 0.0) {
            return true;
        }
    }
    return false;
}

bool intersect_cylinder(in Ray R, in Cylinder C, out float t,  out bool devant, out vec3 color) {

    vec3 x = R.Dir - dot(C.Dir,R.Dir)*C.Dir;
    vec3 y = R.Origin - C.Center - dot(C.Dir,R.Origin-C.Center)*C.Dir;
    float a = dot(x,x);
    float b = 2.0*dot(x,y);
    float c = dot(y,y) - C.r*C.r;

    float delta = b*b - 4.0*a*c;   


    if (delta > 0.0) {
        float t1 = (-b - sqrt(delta)) / (2.0*a);
        float t2 = (-b + sqrt(delta)) / (2.0*a);
        t = t1;
        float l = dot(C.Dir,C.Center - (R.Origin + t*R.Dir));
        devant = true;
        if ( C.h > 0.0 && (-(C.h /2.0) > l || l > C.h/2.0 )) {
            t = t2;
            devant = false;
            //color = vec3(1,0,0);//rouge
            l = dot(C.Dir,C.Center - (R.Origin + t*R.Dir));
            if ( C.h > 0.0 && (-(C.h /2.0) > l || l > C.h/2.0 )) {
                t=-1.0;
                return false;
            }
        }

        if (t < 0.0) {
            t = t2;
            devant = false;
            //color = vec3(0,1,0);//vert
            if (t < 0.0) {t=-1.0; return false;}
        }

    } else if (delta == 0.0) {
        t = (-b - sqrt(delta)) / (2.0*a);
        //color = vec3(0,0,1); //bleu
        if (t < 0.0) {t=-1.0; return false;}
    } else {
        t = -1.0;
        return false;
    } 
    return true;
}

bool intersect_cone(in Ray R, in Cone C, out float t, out bool devant) {

    vec3 x = R.Dir - dot(C.Dir,R.Dir)*C.Dir;
    vec3 y = R.Origin - C.Center - dot(C.Dir,R.Origin-C.Center)*C.Dir;
    float a1 = dot(x,x);
    float b1 = 2.0*dot(x,y);
    float c1 = dot(y,y);
    float a2 = C.r*C.r/(C.s*C.s)*pow(dot(C.Dir,R.Dir),2.0);
    float b2 = 2.0*C.r*C.r/(C.s*C.s)*dot(C.Dir,R.Dir)*dot(C.Dir,R.Origin-C.Center) - 2.0*C.r*C.r/C.s*dot(C.Dir,R.Dir);
    float c2 = C.r*C.r/(C.s*C.s)*pow(dot(C.Dir,R.Origin-C.Center),2.0) - 2.0*C.r*C.r/C.s*dot(C.Dir,R.Origin-C.Center) + C.r*C.r;
    float a = a1 -a2;
    float b = b1- b2;
    float c = c1- c2;


    float delta = b*b - 4.0*a*c;   


    if (delta > 0.0) {
        float t1 = (-b - sqrt(delta)) / (2.0*a);
        float t2 = (-b + sqrt(delta)) / (2.0*a);
        t = t1;
        float l = dot(C.Dir,C.Center +(R.Origin + t*R.Dir));
        devant = true;
        if ( C.h > 0.0 && (0.0 > l || l > C.h )) {
            t = t2;
            devant = false;
            //color = vec3(1,0,0);//rouge
            l = dot(C.Dir,C.Center + (R.Origin + t*R.Dir));
            if ( C.h > 0.0 && (0.0 > l || l > C.h )) {
                t=-1.0;
                return false;
            }
        }

        if (t < 0.0) {
            t = t2;
            devant = false;
            //color = vec3(0,1,0);//vert
            if (t < 0.0) {t=-1.0; return false;}
        }

    } else if (delta == 0.0) {
        t = (-b - sqrt(delta)) / (2.0*a);
        //color = vec3(0,0,1); //bleu
        if (t < 0.0) {t=-1.0; return false;}
    } else {
        t = -1.0;
        return false;
    } 
    return true;
}

vec3 apply_light_sphere(in Ray R, in Sphere S, in Lum L, in float t, in Sphere spheres[NB_SPHERE], in Cylinder cylinders[NB_CYL], in Cone cones[NB_CONE], in int i) {
    float costheta = dot(normalize(S.Center - (R.Origin + t*R.Dir)),normalize((R.Origin + t*R.Dir) - L.Pos));
    if (costheta < 0.0){costheta=0.0;}

    Ray Rl;

    Rl.Origin = R.Origin + t*R.Dir;
    Rl.Dir = L.Pos - Rl.Origin;

    //Ombres sphères sur sphères
    float t2 = -1.0;
    for(int j = 0; j < NB_SPHERE; j++) {
        if (j != i && length(spheres[j].Center - L.Pos) > 0.1) {
            t2 = -1.0;
            if (intersect_sphere(Rl, spheres[j], t2)){
                if (t2 > 0.01 && t2 < 0.99) {
                    return vec3(0,0,0);
                }  
            }
        }
    }

    //Ombres plan sur sphère
    t2 = -1.0;
    for(int j = 0; j < NB_PLAN; j++) {
        if (intersect_plan(Rl, plans(j), t2)){
            if (t2 > 0.01 && t2 < 0.99) {
                return vec3(0,0,0);
            }  
        }
    }

    //Ombres cylindres sur sphère
    t2 = -1.0;
    for(int j = 0; j < NB_CYL; j++) {
        bool devant;
        vec3 color;
        if (intersect_cylinder(Rl, cylinders[j], t2, devant, color)){
            if (t2 > 0.01 && t2 < 0.99) {
                return vec3(0,0,0);
            }  
        }
    }

    //Ombres cones sur sphère
    t2 = -1.0;
    for(int j = 0; j < NB_PLAN; j++) {
        bool devant;
        if (intersect_cone(Rl, cones[j], t2, devant)){
            if (t2 > 0.01 && t2 < 0.99) {
                return vec3(0,0,0);
            }  
        }
    }
    return vec3(S.Color*L.Color*costheta);
}

vec3 apply_light_plan(in Ray R, in Plan P, in Lum L, in float t, in Sphere spheres[NB_SPHERE],  in Cylinder cylinders[NB_CYL], in Cone cones[NB_CONE], in int i) {
    vec3 n = cross(P.Dir1,P.Dir2);
    float costheta = dot(normalize(n),normalize((R.Origin + t*R.Dir)- L.Pos));
    if (costheta < 0.0){costheta=0.0;}

    Ray Rl;

    Rl.Origin = R.Origin + t*R.Dir;
    Rl.Dir = L.Pos - Rl.Origin;

    //Ombres plan sur plan
    float t2 = -1.0;
    for(int j = 0; j < NB_PLAN; j++) {
        if (j != i) {
            t2 = -1.0;
            if (intersect_plan(Rl, plans(j), t2)){
                if (t2 > 0.01 && t2 < 0.99) {
                    return vec3(0,0,0);
                }  
            }
        }
    }

    //Ombres sphère sur plan
    t2 = -1.0;
    for(int j = 0; j < NB_SPHERE; j++) {
        if (intersect_sphere(Rl, spheres[j], t2)){
            if (t2 > 0.01 && t2 < 0.99) {
                return vec3(0,0,0);
            }  
        }
    }

    //Ombres cylindres sur plan
    t2 = -1.0;
    for(int j = 0; j < NB_CYL; j++) {
        bool devant;
        vec3 color;
        if (intersect_cylinder(Rl, cylinders[j], t2, devant, color)){
            if (t2 > 0.01 && t2 < 0.99) {
                return vec3(0,0,0);
            }  
        }
    }

    //Ombres cones sur plan
    t2 = -1.0;
