        bool devant;
        if (intersect_cone(Rl, cones[j], t2, devant)){
            if (t2 > 0.01 && t2 < 0.99) {
                return vec3(0,0,0);
            }  
        }
    }
    return vec3(P.Color*L.Color*costheta*L.I);
}

vec3 apply_light_cylinder(in Ray R, in Cylinder C, in Lum L, in float t, bool devant, in Sphere spheres[NB_SPHERE],  in Cylinder cylinders[NB_CYL],in Cone cones[NB_CONE], in int i) {
    float costheta = dot(normalize(C.Center - (R.Origin + t*R.Dir)),normalize((R.Origin + t*R.Dir) - L.Pos));
    if (!devant) {
        costheta = dot(-normalize(C.Center - (R.Origin + t*R.Dir)),normalize((R.Origin + t*R.Dir) - L.Pos));
    }
    if (costheta < 0.0){costheta=0.0;}


    Ray Rl;

