                }  
            }
        }
    }

    //Ombres plan sur cylindre
    t2 = -1.0;
    for(int j = 0; j < NB_PLAN; j++) {
        if (intersect_plan(Rl, plans(j), t2)){
            if (t2 > 0.01 && t2 < 0.99) {
                return vec3(0,0,0);
            }  
        }
    }

    //Ombres sphère sur cylindre
    t2 = -1.0;
    for(int j = 0; j < NB_SPHERE; j++) {
        if (intersect_sphere(Rl, spheres[j], t2)){
            if (t2 > 0.01 && t2 < 0.99) {
                return vec3(0,0,0);
            }  
        }
    }

    //Ombres cones sur cylindre
    t2 = -1.0;
    for(int j = 0; j < NB_PLAN; j++) {
        bool devant;
        if (intersect_cone(Rl, cones[j], t2, devant)){
            if (t2 > 0.01 && t2 < 0.99) {
                return vec3(0,0,0);
            }  
        }
    }

    return vec3(C.Color*L.Color*costheta);
}

