       float t;     
       bool devant;
       if (intersect_cylinder(R, cylinders[i], t, devant, cylinders[i].Color)) {
           if (t>0.0 && t< best_dist) {
               best_dist = t;
               fragColor = vec4(0,0,0,1);
               for(int j = 0; j < NB_LUM; j++) { //pour toutes les lumieres
                   fragColor = vec4(fragColor.xyz + apply_light_cylinder(R,cylinders[i], lumieres[j], best_dist,devant, spheres, cylinders, cones, i) ,1);
               }
           }
       }
    }
    //Intersection cone Camera+Lumiere+Ombres
    best_dist = 999999.0;
    for(int i = 0; i < NB_CONE; i++) { //pour toutes les cones
       Ray R = launch(C, fragCoord);
       float t;     
       bool devant;
       if (intersect_cone(R, cones[i], t, devant)) {
           if (t>0.0 && t< best_dist) {
               best_dist = t;
               fragColor = vec4(0,0,0,1);
               for(int j = 0; j < NB_LUM; j++) { //pour toutes les lumieres
                   fragColor = vec4(fragColor.xyz + apply_light_cone(R,cones[i], lumieres[j], best_dist,devant, spheres, cylinders, cones, i) ,1);
               }
           }
       }
    }


}
