//vec3 col = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float speed = .2;
    float scale = .002;

    vec2 uv = fragCoord*scale;

    for(float i=1.;i<10.;i++){
        uv.x += .5/i*sin(i*3.*uv.y+iTime*speed);
        uv.y += .3/i*cos(i*3.*uv.x+iTime*speed);
    }

    float r = .5+.5*cos(uv.x+uv.y+1.);
    float g = .3+.5*sin(uv.x+uv.y+1.);
    float b = .85+.5*cos(sin(uv.x+uv.y)+cos(uv.x+uv.y));
    vec3 col = vec3(r,g,b);
    fragColor = vec4(col,1.);
}
