#define cRandColorA1 vec3(0.5, 0.5, 0.5)
#define cRandColorA2 vec3(0.5, 0.5, 0.5)
#define cRandColorB1 vec3(1.0, 1.0, 1.0)
#define cRandColorB2 vec3(0.0, 0.33, 0.67)
#define cRandColorC vec3(1.0, 0.33, 0.67)
#define cRandColorD vec3(0.0, 0.33, 0.27)
#define cRandColorE vec3(0.0, 0.33, 0.47)
#define cRandA 1.0
#define cRandB 1.0
#define cRandC 1.0
#define cRandNum 16.0
#define cRandEmission 1.0
#define cEmissionScrolling vec4(1, 1, 1, 1)
#define cEmissionColor vec3(1, 0.0, 0.0)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    vec4 DecalColor = texture(iChannel0, uv);

    vec3 RandColorA = mix(cRandColorA1, cRandColorA2, cRandA);
    vec3 RandColorB = mix(cRandColorB1, cRandColorB2, cRandB);

    float decalColorIndex = floor(fract(DecalColor.r + cRandC) * cRandNum);
    vec3 emissionDecal = DecalColor.ggg; 
    float DecalDetail = DecalColor.b;

    if(decalColorIndex == 0.0)
    {
        DecalColor.rgb = RandColorA;
    }
    else if(decalColorIndex == 1.0)
    {
        DecalColor.rgb = RandColorB;
    }
    else if(decalColorIndex == 2.0)
    {
        DecalColor.rgb = cRandColorC;
    }
    else if(decalColorIndex == 3.0)
    {
        DecalColor.rgb = cRandColorD;
    }
    else
    {
        DecalColor.rgb = cRandColorE;
    }

    vec4 BaseColor =texture(iChannel1, uv);
    DecalColor.rgb *= DecalDetail;

    vec3 final = mix(BaseColor.rgb, DecalColor.rgb, DecalColor.a);
    final += emissionDecal * cRandEmission * DecalColor.rgb;

    vec2 EmissionUV = uv * cEmissionScrolling.xy + fract(iTime * cEmissionScrolling.zw);
    vec3 EmissionFlow = texture(iChannel2, EmissionUV).rgb;
    EmissionFlow *= texture(iChannel3, uv).r * cEmissionColor;

    final += EmissionFlow;

    fragColor = vec4(final, 1.0);
}
