#ifdef GL_ES
precision mediump float;
#endif

float atan2(vec2 point){return mod(degrees(atan(point.y,point.x))+180.,360.);}
vec2 coord2polar(vec2 center,vec2 point){
    point-=center;
    return vec2(length(point),atan2(point));
}
vec2 rot2d(vec2 center,vec2 p, float dir){dir=radians(dir);
    return mat2(cos(dir),sin(dir),
                -sin(dir),cos(dir))*(p-center)+center;
}
vec3 nShape(vec2 center, float n, float size, float rot, vec2 st, vec3 bc, vec3 sc){
    float dir=360./n,plot;
    vec2 pst=coord2polar(center,st);
    float cnt=floor(pst.y/dir+0.5);
    float t=radians(mod(pst.y+rot*dir,dir)-0.5*dir);
    vec2 p =rot2d(center,center+vec2(size,size*tan(t)),cnt*dir);
    plot =smoothstep(0., 2./iResolution.y, coord2polar(center,p).x - pst.x);
    bc=mix(bc,sc,plot);
    return bc;
}


void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 st = ( fragCoord - .5*iResolution.xy )/iResolution.y;
    vec3 color = vec3(0.);
    for(int i=1; i<7; i++){
    	color=nShape(vec2(-0.5+0.15*float(i)),float(i)+2.,0.07,0.748,st,color,vec3(1.));
    }
    fragColor = vec4(color,1.0);
}
