void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized coordinates
    vec2 p = (2.0 * fragCoord - iResolution.xy) / min(iResolution.y, iResolution.x);

    //
    vec2 uv = p;
    float intensity = 0.0;
    float scale = 1.0;

    for (int i = 0; i < 5; i++) {
        intensity += sin(uv.x * scale + iTime * 0.5) * sin(uv.y * scale + iTime * 0.3);
        scale *= 2.0;
    }

    intensity = 0.5 + 0.5 * intensity;

    // Color gradients
    vec3 blueColor = vec3(0.0, 0.5, 1.0);
    vec3 greenColor = vec3(0.0, 1.0, 0.5);
    vec3 finalColor = mix(blueColor, greenColor, intensity);

    // Output to screen
    fragColor = vec4(finalColor, 1.0);
}
