void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.y;
    vec2 center = (iResolution.xy*0.5)/iResolution.y;
    vec2 start = center;
    vec2 end = center+0.33333*vec2(cos(iTime), sin(iTime));
    float f = smoothstep(0.5, 0.0, distance(uv, end));
    start = mix(
    start,start+end*vec2(cos(iTime+uv.x+60.*iTime*uv.y), sin(iTime+uv.y+60.*iTime*uv.x)),f);
    float pf = dot(uv, start-end);
    vec3 gc = vec3(abs(pf), f, abs(f-pf));
    vec2 pa = uv - start; vec2 ba = end - start; 
    fragColor = vec4(clamp(1.0 - length(pa - ba * clamp(
        dot(pa, ba) / dot(ba, ba), 0.0, 1.0))/0.05, 0.0, 1.0)*mix(vec3(1.0), gc, f),1.0);
}
