void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float t =fract((iTime/20.)/16.)*16.;
    vec2 uv =fragCoord/(iResolution.xy*1.);    
    vec2 l =uv*2.-1.;

    for(float i;i<64.;i+=1.){
    l=abs(l);
    l-=0.75;
    l*=1.1;
    l*=mat2(
    cos(t),-sin(t),
    sin(t),cos(t)
    );
}
    float j=length(l);
    fragColor = vec4(j*j,j*j*j,j*j*j*j,1.0);
}
