#define pi 3.14159265

vec3 color(float speed, float dependent, float offset)
{
    float c = speed * dependent + offset;

    float r = c + pi/2.0;
    float g = c + 7.0*pi/6.0;
    float b = c + 11.0*pi/6.0;

    return abs(sin(vec3(r, g, b)));
}

float wiggleRing(vec2 plane, float radius, float thickness, float nWiggle, float aWiggle, float aOffset)
{
    float angle = atan(plane.x,plane.y)/(2.0*pi) + aOffset;

    float wiggleOffset = aWiggle*cos(nWiggle*2.0*pi*angle);

    float shape = thickness/abs(length(plane)-radius*(1.0-wiggleOffset));

    return shape;
}

float wiggleRingField(vec2 plane, float nRing, float sRing, float rV, float thickness, float nWiggle, float aWiggle)
{
    float field = 0.0;
    for(float i = 0.0; i < nRing; i += sRing)
    {

        float ring = wiggleRing(plane, mod(i+rV, nRing), thickness, nWiggle, aWiggle, (0.1/nWiggle)*i+0.05*rV);
        field += ring;
    }

    return field;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = 5.0*(fragCoord-0.5*iResolution.xy)/iResolution.y;
    vec3 col = vec3(0.0);

    float smoothTime = smoothstep(0.5, 1.0, fract(iTime)) + floor(iTime);
    float angle = atan(uv.x, -uv.y)/(2.0*pi);


    float wiggleNum = mod(0.1*iTime, 20.0);
    float t = mod(0.1*iTime, 25.0);

    float wrf1 = wiggleRingField(vec2(uv.x,uv.y), 10.0, 0.5, t, 0.005, wiggleNum, 0.5);
    float wrf2 = wiggleRingField(vec2(-uv.x,uv.y), 10.0, 0.5, t, 0.005, wiggleNum, 0.5);
    float wrf3 = wiggleRingField(vec2(uv.x,-uv.y), 10.0, 0.5, t, 0.005, wiggleNum, 0.5);
    float wrf4 = wiggleRingField(vec2(-uv.x,-uv.y), 10.0, 0.5, t, 0.005, wiggleNum, 0.5);

    col += smoothstep(0.0, 0.5, length(uv))*(wrf1+wrf2+wrf3+wrf4)*color(1.0, pi*angle, 0.5*iTime);


    fragColor = vec4(col,1.0);
}
