const vec2 p1 = vec2(.7, .5);
const vec2 p2 = vec2(.4, .2);
const vec2 p3 = vec2(.4, .8);
const mat3 tri_mat = inverse(mat3(p2-p1,0,p3-p1,0,p1,1));

const vec3 bg = vec3(0,0,0);
const mat3 colors = mat3(1,0,0, 0,1,0, 0,0,1);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    vec2 tri_uv = vec2(tri_mat * vec3(uv, 1));

    fragColor = vec4(mix(bg, 
        colors * vec3(tri_uv, 1.-tri_uv.x-tri_uv.y),
        float(tri_uv.x>0.&&tri_uv.y>0.&&tri_uv.y+tri_uv.x<1.)
    ),1.0);
}
