float Circle(vec2 uv, vec2 pos, float raio, float blur)
{
    float distancia = length(uv - pos);
    float c = smoothstep(raio, raio-blur, distancia);

    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy / iResolution.xy);
    uv -= .5;
    uv.x *= iResolution.x/iResolution.y;

    //float d = length(uv);
    //float r = .3;

    // Output to screen
    //fragColor = vec4(vec3(uv.x, uv.y, 0),1.0);

    //float c = smoothstep(r, r-0.005, d);

    float c = Circle(uv, vec2(.2, .1), .4, 0.005);
    c+= Circle(uv, vec2(-.18, 0), .1, 0.005);
    c+= Circle(uv, vec2(.58, 0), .1, 0.005);
    c+= Circle(uv, vec2(.2, .5), .4, 0.005);
    c+= Circle(vec2(uv.x * 1.4, uv.y), vec2(.28, -.68), .4, 0.005);

    float c2 = Circle(uv, vec2(.2, .5), .2, 0.005);
    c2 += Circle(uv, vec2(0.025, .05), .05, 0.005);
    c2 += Circle(uv, vec2(0.36, .05), .05, 0.005);

    fragColor = vec4(vec3(c-c2),1.0);
}
