vec4 plus(vec2 uv, float size, vec3 color) {
 ;
  // float d = length(uv);
    vec4 col = vec4(vec3(color), 1.0);
    float d =  length(max(abs(uv)-size,0.0));
    col.a = smoothstep(size, size-0.005, d);
    return col;

}
vec4 circle(vec2 uv, float r, vec3 color) {
    float d = length(uv);
    vec4 col = vec4(vec3(color), 1.);
    col.a = smoothstep(r, r - .005, d);
    return col;
}

vec4 background(vec2 uv, vec3 color){

}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
      vec3 plusColor = vec3(0.50, .75, .20);

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
     vec2 location = iMouse.xy;
      vec2 m = iMouse.xy / iResolution.xy;
    m -= .5;


      uv -= 0.5;
      uv.x *= iResolution.x/iResolution.y;
      m.x *= iResolution.x/iResolution.y;
    if(iMouse.x == 0.0 && iMouse.y == 0.0) uv = uv; else uv = uv - m;

    vec4 col = vec4(0.);
    col = mix(col, background(uv, plusColor));

        vec4 circle1 = circle(uv, 0.3, vec3(.19, .55, .1));
        col = mix(col, circle1, circle1.a);



         vec4 plusC = plus(vec2(uv.x, uv.y), 0.03, plusColor);
        col = mix(col, plusC, plusC.a);

           vec4 plus1 = plus(vec2(uv.x-0.1, uv.y), 0.03, plusColor);
        col = mix(col, plus1, plus1.a);

            vec4 plus2 = plus(vec2(uv.x+0.1, uv.y), 0.03, plusColor);
        col = mix(col, plus2, plus2.a);

        vec4 plus3 = plus(vec2(uv.x, uv.y+0.1), 0.03, plusColor);
        col = mix(col, plus3, plus3.a);

         vec4 plus4 = plus(vec2(uv.x, uv.y-0.1), 0.03, plusColor);
        col = mix(col, plus4, plus4.a);

        vec4 circle2 = circle(uv, 0.03+sin(iTime)*0.025, vec3(.9, .25, .1));
        col = mix(col, circle2, circle2.a);


fragColor = vec4(col);

}
