// go to buffer A
void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;

    vec4 info = texture(iChannel0, uv);

    fragColor = info;
}

// set to 0 if you want it to stay still
#define scroll 1


//spectrogram
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord/iResolution.xy;

#if scroll
    if(fragCoord.x > iResolution.x - 1.) {
        fragColor = texture(iChannel1, vec2(uv.y, 0)).rrrr;
    } else {
        fragColor = texture(iChannel0, uv + vec2(0.001, 0.));
    }
#else
    if(uv.x > iTime/3.) {
        fragColor = texture(iChannel1, vec2(uv.y, 0)).rrrr;
    } else {
        fragColor = texture(iChannel0, uv);
    }
#endif



}

// useless buffer
