float f_n_dist_sqr(vec2 o){
    return max(abs(o.x), abs(o.y));
}
float f_nor_inout(float n){
    float b_gt1 = float((n*2.)>1.);
    return (1.-b_gt1)*(n*2.) + b_gt1 * (2. - (n*2.)); 
}
void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 o_trpino = (fragCoord.xy-iResolution.xy*0.5)/iResolution.y;

    float n = f_n_dist_sqr(o_trpino);
    n = f_nor_inout(n);

    float n_its = 5.;
    float n_min = 1.;
    vec2 o = o_trpino;
    float n_tau = 6.2831;
    vec3 o_col = vec3(0.);
    for(float n_it_nr = 0.; n_it_nr < 1.; n_it_nr+=1./n_its){
        float n_ang = n_tau*n_it_nr;
        vec2 o_rtdt = vec2(
            cos(n_ang)*o.x + -sin(n_ang)*o.y,
            sin(n_ang)*o.x + cos(n_ang)*o.y
        );
        n = f_n_dist_sqr(o_rtdt);
        n = f_nor_inout(n);
        n = fract(n*n_its);
        if(n < n_min){
            n_min = n;
            o_col = vec3(n_it_nr);
        }
    }
    float n_dist = length(o_trpino);
    fragColor = vec4(
        n_min*n_dist,
        0., 
        o_col.b*n_dist*n_dist*n_dist*n_min+sin(n_dist*n_its*n_tau)*0.2, 
        1.
    );

}
