float box(in vec2 _xy, in vec2 _size){
               _size = vec2(0.5) - _size*0.5;
               vec2 uv = step(_size, _xy);
               uv *= step(_size, vec2(1.0)-_xy);
               return uv.x*uv.y;
            }
            float drawCross(in vec2 _xy, float _size){
                return  box(_xy, vec2(_size,_size*0.25)) +
                        box(_xy, vec2(_size*0.25,_size));
            }

vec4 circleWithPlus(vec2 uv, float r, vec3 color) {

    vec3 myCross = vec3(drawCross(uv+0.5, 0.1));
    float d = length(uv);
    vec4 col = vec4(vec3(color+ myCross) , 0.0);
    col.a = smoothstep(r, r - .005, d);

    return col;
}

vec4 circle(vec2 uv, float r, vec3 color) {
    float d = length(uv);
    vec4 col = vec4(vec3(color), 1.);
    col.a = smoothstep(r, r - .005, d);

    return col;
}

