void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec3 color = vec3(0.0);


    color.r = abs(sin(uv.x * 8.0 + iTime * 4.0));
    color.g = abs(cos(uv.y * 8.0 + iTime * 2.0));
    color.b = abs(sin(uv.x * uv.y + iTime * 8.0));

    fragColor = vec4(color, 1.0);
}
